(859, 'wpmletmessage_posts', '<div class="wpmlposts">\n		[newsletters_post_loop]\n			<div class="wpmlpost">\n				<h3>[newsletters_post_anchor][newsletters_post_title][/newsletters_post_anchor]</h3>\n				[newsletters_post_date_wrapper]<p><small>Posted on [newsletters_post_date format="F jS, Y"] by [newsletters_post_author]</small></p>[/newsletters_post_date_wrapper]\n				<div class="wpmlpost_content">\n					[newsletters_post_thumbnail]\n					[newsletters_post_excerpt]\n				</div>\n			</div>\n			<hr style="visibility:hidden; clear:both;" />\n		[/newsletters_post_loop]\n	</div>', 'yes'),
(860, 'wpmletsubject_latestposts', '', 'yes'),
(861, 'wpmletmessage_latestposts', '<div class="wpmlposts">\n		[newsletters_post_loop]\n			<h2>[newsletters_category_heading]</h2>\n			<div class="wpmlpost">\n				<h3>[newsletters_post_anchor][newsletters_post_title][/newsletters_post_anchor]</h3>\n				[newsletters_post_date_wrapper]<p><small>Posted on [newsletters_post_date format="F jS, Y"] by [newsletters_post_author]</small></p>[/newsletters_post_date_wrapper]\n				<div class="wpmlpost_content">\n					[newsletters_post_thumbnail]\n					[newsletters_post_excerpt]\n				</div>\n			</div>\n			<hr style="visibility:hidden; clear:both;" />\n		[/newsletters_post_loop]\n	</div>', 'yes'),
(862, 'wpmletsubject_sendas', '', 'yes'),
(863, 'wpmletmessage_sendas', '<div class="newsletter_posts">\n		[newsletters_post_loop]\n			<div class="newsletter_post newsletter_sendas">\n				[newsletters_post_date_wrapper]<p><small>Posted on [newsletters_post_date format="F jS, Y"] by [newsletters_post_author]</small></p>[/newsletters_post_date_wrapper]\n				<div class="newsletter_post_content">\n					[newsletters_post_excerpt]\n				</div>\n			</div>\n		[/newsletters_post_loop]\n	</div>', 'yes'),
(864, 'wpmletsubject_confirm', 'Confirm Subscription', 'yes'),
(865, 'wpmletmessage_confirm', 'Good day,\r\n\r\nThank you for subscribing to the mailing list(s): [newsletters_mailinglist].\r\nPlease click the link below to activate/confirm your subscription.\r\n\r\n[newsletters_activate]\r\n\r\nAll the best,\r\n[newsletters_blogname]', 'yes'),
(866, 'wpmletsubject_bounce', 'Email Bounced', 'yes'),
(867, 'wpmletmessage_bounce', 'Good day,\r\n\r\nAn email has bounced.\r\nThe email address is: [newsletters_email].\r\nTotal bounces for this email/subscriber: [newsletters_bouncecount].\r\n\r\nAll the best,\r\n[newsletters_blogname]', 'yes'),
(868, 'wpmletsubject_unsubscribe', 'Unsubscription', 'yes'),
(869, 'wpmletmessage_unsubscribe', 'Good day Administrator,\r\n\r\nA subscriber has unsubscribed from a mailing list.\r\nThe mailing list is: [newsletters_mailinglist].\r\nThe subscriber email is: [newsletters_email].\r\n\r\n[newsletters_unsubscribecomments]\r\n\r\nAll the best,\r\n[newsletters_blogname]', 'yes'),
(870, 'wpmletsubject_unsubscribeuser', 'You are unsubscribed', 'yes'),
(871, 'wpmletmessage_unsubscribeuser', 'Your e-mail has been removed from our database.\r\nYou will no longer receive communication from us.\r\nThanks for your readership, and we hope you''ll visit us again!\r\n\r\nWas this a mistake? If it was, you can [newsletters_resubscribe]', 'yes'),
(872, 'wpmletsubject_unsubscribeconfirm', 'Confirm Unsubscribe', 'yes'),
(873, 'wpmletmessage_unsubscribeconfirm', 'Good day,\r\n\r\nPlease confirm your unsubscribe:\r\n\r\n[newsletters_unsubscribelink]\r\n\r\nAll the best,\r\n[newsletters_blogname]', 'yes'),
(874, 'wpmletsubject_expire', 'Subscription Expired', 'yes'),
(875, 'wpmletmessage_expire', 'Good Day,\r\n\r\nYour subscription has expired.\r\nThe mailing list is: [newsletters_mailinglist].\r\nPlease click the link below to renew your subscription.\r\n\r\n[newsletters_activate]\r\n\r\nAll the best,\r\n[newsletters_blogname]', 'yes'),
(876, 'wpmletsubject_order', 'Paid Subscription', 'yes'),
(877, 'wpmletmessage_order', 'Good day Administrator,\r\n\r\nYou have received a paid mailing list subscription order.\r\nThe mailing list is: [newsletters_mailinglist].\r\nThe subscriber email is: [newsletters_email].\r\n\r\nAll the best,\r\n[newsletters_blogname]', 'yes'),
(878, 'wpmletsubject_schedule', 'Email Cron Fired', 'yes'),
(879, 'wpmletmessage_schedule', 'Good day Administrator,\r\n\r\nYour email cron has been fired as scheduled.\r\n\r\nAll the best,\r\n[newsletters_blogname]', 'yes'),
(880, 'wpmletsubject_import_complete', 'Import Completed', 'yes'),
(881, 'wpmletmessage_import_complete', 'Good day,\r\n\r\nAll the subscribers you imported have finished importing in the background.\r\n\r\nAll the best,\r\n[newsletters_blogname]', 'yes'),
(882, 'wpmletsubject_queue_complete', 'Queue Sending Completed', 'yes'),
(883, 'wpmletmessage_queue_complete', 'Good day,\r\n\r\nAll queued emails have been sent out.\r\n\r\nAll the best,\r\n[newsletters_blogname]', 'yes'),
(884, 'wpmletsubject_subscribe', 'New Subscription', 'yes'),
(885, 'wpmletmessage_subscribe', 'Good day Administrator,\r\n\r\nA user/visitor has just subscribed to: [newsletters_mailinglist].\r\nThe email address of this subscriber is: [newsletters_email].\r\n\r\n[newsletters_customfields]\r\n\r\nAll the best,\r\n[newsletters_blogname]', 'yes'),
(886, 'wpmletsubject_authenticate', 'Authenticate Email Address', 'yes'),
(887, 'wpmletmessage_authenticate', 'Please authenticate your subscriber account by clicking the link below.\r\n\r\n[newsletters_authenticate]\r\n\r\nOnce you authenticate, you can manage your subscriptions and additional subscriber information.\r\n\r\nAll the best,\r\n[newsletters_blogname]', 'yes'),
